package assignments;

public class Asgmnt25_evenoddno1to100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//if(no%2==0){even} else {odd}
		
		
		for(int i=1; i<=100; i++){
			
			if (i%2==0) 
			{
				System.out.println(i+" is even number");
				
			}
				
			else
			{
				System.out.println(i+" is odd number");
			}
			
		}
			
		
	
	}

}
